command:
// If your token art didn't properly update to the new standard
// And you are using the default art with no custom path
// You can use this macro to update your Token's Art

// UPDATE TOKENS TO NEW DEFAULT
// WILL OVERRIDE AND WON'T CHECK OLD IMAGES
// IF YOU HAVE CUSTOM IMAGES DON'T RUN THIS
const pokemon = game.actors.filter(a => a.type === "pokemon");
for(const mon of pokemon) {
  const match = /(\d+.+).(webp|png)/.exec(mon.prototypeToken.texture.src);
  if(!match) continue;

  const[_, name, ext] = match;
  const newUrl = `/systems/ptu/static/images/sprites/`;

  mon.update({"prototypeToken.texture.src":  `${newUrl}${name}.webp`});
  for(const token of mon.getActiveTokens()) {
    token.document.update({"texture.src": `${newUrl}${name}.webp`});
  }
}
